import React, { useState } from 'react';
import axios from 'axios';

const FirebaseCodeGenerator = () => {
  const [code, setCode] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      // URL do Firebase Realtime Database
      const url = 'https://geo-finder-7e641-default-rtdb.europe-west1.firebasedatabase.app/codes.json';

      // Dados a serem enviados
      const data = {
        code: code
      };

      // Envia os dados usando Axios com o método PUT
      await axios.put(url, data);

      setSuccessMessage('Código enviado com sucesso!');
      setCode('');
    } catch (error) {
      console.error('Erro ao enviar código para o Firebase:', error);
      setErrorMessage('Ocorreu um erro ao enviar o código. Por favor, tente novamente.');
    }
  };

  return (
    <div>
      <h2>Gerador de Código</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Código:
          <input
            type="text"
            value={code}
            onChange={(e) => setCode(e.target.value)}
          />
        </label>
        <button type="submit">Enviar para o Firebase</button>
      </form>
      {successMessage && <p style={{ color: 'green' }}>{successMessage}</p>}
      {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}
    </div>
  );
};

export default FirebaseCodeGenerator;
