import React, { useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const FirebaseCodeGenerator = () => {
  const [code, setCode] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const generateRandomCode = () => {
    const characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789!@#$%';
    const length = 10;
    let generatedCode = '';

    for (let i = 0; i < length; i++) {
      const randomIndex = Math.floor(Math.random() * characters.length);
      generatedCode += characters[randomIndex];
    }

    return generatedCode;
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const generatedCode = generateRandomCode();
      setCode(generatedCode);

      if ('geolocation' in navigator) {
        navigator.geolocation.getCurrentPosition(async (position) => {
          const { latitude, longitude } = position.coords;

          const data = {
            code: generatedCode,
            latitude: latitude,
            longitude: longitude
          };

          const url = `https://geo-finder-7e641-default-rtdb.europe-west1.firebasedatabase.app/codes/${generatedCode}.json`;

          await axios.put(url, data);

          setSuccessMessage('Código enviado com sucesso!');
        }, (error) => {
          console.error('Erro ao obter localização:', error);
          setErrorMessage('Ocorreu um erro ao obter sua localização. Por favor, tente novamente.');
        });
      } else {
        setErrorMessage('Geolocalização não suportada pelo seu navegador.');
      }
    } catch (error) {
      console.error('Erro ao enviar código para o Firebase:', error);
      setErrorMessage('Ocorreu um erro ao enviar o código. Por favor, tente novamente.');
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      <div className="bg-white p-8 rounded shadow-md text-center">
        <h2 className="text-2xl font-bold mb-4">Gerador de Código</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          <label className="block">
            <span className="block text-gray-700">Código Gerado:</span>
            <input
              type="text"
              value={code}
              readOnly
              className="mt-1 block w-full px-3 py-2 bg-gray-100 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
            />
          </label>
          <button
            type="submit"
            className="w-full px-4 py-2 bg-blue-500 text-white font-semibold rounded-md hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-opacity-50"
          >
            Gerar e Enviar para o Firebase
          </button>
        </form>
        {successMessage && <p className="mt-4 text-green-500">{successMessage}</p>}
        {errorMessage && <p className="mt-4 text-red-500">{errorMessage}</p>}
        <Link to="/finder" className="mt-4 inline-block text-blue-500 hover:underline">
          Ir para finder
        </Link>
      </div>
    </div>
  );
};

export default FirebaseCodeGenerator;
