import React, { useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import backgroundImage from './background.png';

const FirebaseCodeGenerator = () => {
  const [code, setCode] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const generateRandomCode = () => {
    const characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789!@#$%';
    const length = 10;
    let generatedCode = '';

    for (let i = 0; i < length; i++) {
      const randomIndex = Math.floor(Math.random() * characters.length);
      generatedCode += characters[randomIndex];
    }

    return generatedCode;
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const generatedCode = generateRandomCode();

      setCode(generatedCode);

      if ('geolocation' in navigator) {
        navigator.geolocation.getCurrentPosition(async (position) => {
          const { latitude, longitude } = position.coords;

          const data = {
            code: generatedCode,
            latitude: latitude,
            longitude: longitude
          };

          const url = `https://geo-finder-7e641-default-rtdb.europe-west1.firebasedatabase.app/codes/${generatedCode}.json`;

          await axios.put(url, data);

          setSuccessMessage('Código enviado com sucesso!');
        }, (error) => {
          console.error('Erro ao obter localização:', error);
          setErrorMessage('Ocorreu um erro ao obter sua localização. Por favor, tente novamente.');
        });
      } else {
        setErrorMessage('Geolocalização não suportada pelo seu navegador.');
      }
    } catch (error) {
      console.error('Erro ao enviar código para o Firebase:', error);
      setErrorMessage('Ocorreu um erro ao enviar o código. Por favor, tente novamente.');
    }
  };

  return (
    <div style={{ textAlign: 'center' }}>
      <div
        style={{
          backgroundImage: `url(${backgroundImage})`,
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          position: 'fixed',
          top: 0,
          left: 0,
          width: '100vw',
          height: '100vh',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center',
          alignItems: 'center',
          padding: '20px',
          boxSizing: 'border-box',
        }}
      >
        <h2 style={{ color: 'white', fontSize: '2em', marginBottom: '20px' }}>Gerador de Código</h2>
        <form onSubmit={handleSubmit} style={{ maxWidth: '100%', width: '300px' }}>
          <label style={{ color: 'white', display: 'block', marginBottom: '10px', fontSize: '1.2em' }}>
            Código Gerado:
            <input
              type="text"
              value={code}
              readOnly
              style={{ marginLeft: '10px', borderRadius: '5px', padding: '5px', fontSize: '1em', width: '100%' }}
            />
          </label>
          <button type="submit" style={{ fontSize: '1em', padding: '10px 20px', marginBottom: '10px' }}>Gerar e Enviar para o Firebase</button>
        </form>
        {successMessage && <p style={{ color: 'green', fontSize: '1em', marginTop: '10px' }}>{successMessage}</p>}
        {errorMessage && <p style={{ color: 'red', fontSize: '1em', marginTop: '10px' }}>{errorMessage}</p>}
        <Link
          to="/finder"
          style={{
            padding: '10px',
            backgroundColor: 'white',
            color: 'black',
            fontWeight: 700,
            display: 'inline-block',
            textDecoration: 'none',
            marginTop: '10px',
            fontSize: '1em',
          }}
        >
          Ir para Finder
        </Link>
      </div>
    </div>
  );
};

export default FirebaseCodeGenerator;
