import React, { useState } from 'react';
import axios from 'axios';

const FirebaseCodeGenerator = () => {
  const [code, setCode] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const generateRandomCode = () => {
    const characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789!@#$%';
    const length = 10;
    let code = '';

    for (let i = 0; i < length; i++) {
      const randomIndex = Math.floor(Math.random() * characters.length);
      code += characters[randomIndex];
    }

    return code;
  };
  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const url = 'https://geo-finder-7e641-default-rtdb.europe-west1.firebasedatabase.app/codes.json';

      // Gerar código aleatório
      const generatedCode = generateRandomCode();

      // Obter localização do usuário
      if ('geolocation' in navigator) {
        navigator.geolocation.getCurrentPosition(async (position) => {
          const { latitude, longitude } = position.coords;

          // Criar um novo ID único para cada entrada
          const newPostKey = firebase.database().ref().child('codes').push().key;

          // Dados a serem enviados
          const data = {
            code: generatedCode,
            latitude: latitude,
            longitude: longitude
          };

          // Define o caminho onde o novo código será salvo
          const updates = {};
          updates['/codes/' + newPostKey] = data;

          // Envia os dados usando Axios com o método PATCH para adicionar um novo nó
          await axios.patch(url, updates);

          setSuccessMessage('Código enviado com sucesso!');
          setCode(generatedCode);
        }, (error) => {
          console.error('Erro ao obter localização:', error);
          setErrorMessage('Ocorreu um erro ao obter sua localização. Por favor, tente novamente.');
        });
      } else {
        setErrorMessage('Geolocalização não suportada pelo seu navegador.');
      }
    } catch (error) {
      console.error('Erro ao enviar código para o Firebase:', error);
      setErrorMessage('Ocorreu um erro ao enviar o código. Por favor, tente novamente.');
    }
  };

  return (
    <div>
      <h2>Gerador de Código</h2>
      <form onSubmit={handleSubmit}>
        <label>
          Código Gerado:
          <input
            type="text"
            value={code}
            readOnly
          />
        </label>
        <button type="submit">Gerar e Enviar para o Firebase</button>
      </form>
      {successMessage && <p style={{ color: 'green' }}>{successMessage}</p>}
      {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}
    </div>
  );
};

export default FirebaseCodeGenerator;
