import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { Link, useNavigate } from 'react-router-dom';
import backgroundImage from './background.png';

const FirebaseCodeGenerator = () => {
  const [code, setCode] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [buttonDisabled, setButtonDisabled] = useState(false);
  const [codes, setCodes] = useState([]);
  const [updateCodeData, setUpdateCodeData] = useState({ code: '', latitude: '', longitude: '' });
  const [hasPaidPlan, setHasPaidPlan] = useState(false);
  const [showPopup, setShowPopup] = useState(false);
  const navigate = useNavigate();
  const intervalTimeRef = useRef(24 * 60 * 60 * 1000);

  useEffect(() => {
    fetchCodes(); // Fetch all codes on component mount

    const savedCode = localStorage.getItem('generatedCode');
    if (savedCode) {
      setCode(savedCode);
      setButtonDisabled(true);
    }

    const interval = setInterval(() => {
      if (code) {
        sendLocationToFirebase(code);
      }
    }, intervalTimeRef.current);

    return () => clearInterval(interval);
  }, [code]);

  const generateRandomCode = () => {
    const characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789!@#$%';
    const length = 10;
    let generatedCode = '';

    for (let i = 0; i < length; i++) {
      const randomIndex = Math.floor(Math.random() * characters.length);
      generatedCode += characters[randomIndex];
    }

    return generatedCode;
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      let generatedCode = code;
      if (!generatedCode) {
        generatedCode = generateRandomCode();
        setCode(generatedCode);
        localStorage.setItem('generatedCode', generatedCode);
        setButtonDisabled(true);
      }

      await sendLocationToFirebase(generatedCode);
      setSuccessMessage('Código enviado com sucesso!');
      fetchCodes(); // Fetch all codes after submission
    } catch (error) {
      console.error('Erro ao enviar código para o Firebase:', error);
      setErrorMessage('Ocorreu um erro ao enviar o código. Por favor, tente novamente.');
    }
  };

  const sendLocationToFirebase = async (currentCode) => {
    try {
      if ('geolocation' in navigator) {
        navigator.geolocation.getCurrentPosition(async (position) => {
          const { latitude, longitude } = position.coords;

          const data = {
            code: currentCode,
            latitude: latitude,
            longitude: longitude
          };

          const url = `https://geo-finder-7e641-default-rtdb.europe-west1.firebasedatabase.app/codes/${currentCode}.json`;

          await axios.put(url, data);

          console.log('Localização enviada para o Firebase:', data);
        }, (error) => {
          console.error('Erro ao obter localização:', error);
        });
      } else {
        console.log('Geolocalização não suportada pelo seu navegador.');
      }
    } catch (error) {
      console.error('Erro ao enviar localização para o Firebase:', error);
    }
  };

  const fetchCodes = async () => {
    try {
      const url = `https://geo-finder-7e641-default-rtdb.europe-west1.firebasedatabase.app/codes.json`;
      const response = await axios.get(url);
      const codesData = response.data ? Object.keys(response.data).map(key => ({
        code: key,
        ...response.data[key]
      })) : [];
      setCodes(codesData);
    } catch (error) {
      console.error('Erro ao buscar códigos do Firebase:', error);
    }
  };

  const updateCode = async () => {
    try {
      const { code, latitude, longitude } = updateCodeData;
      if (!code || !latitude || !longitude) {
        setErrorMessage('Todos os campos são obrigatórios para atualizar um código.');
        return;
      }

      const url = `https://geo-finder-7e641-default-rtdb.europe-west1.firebasedatabase.app/codes/${code}.json`;
      await axios.put(url, { latitude, longitude });
      setSuccessMessage('Código atualizado com sucesso!');
      setUpdateCodeData({ code: '', latitude: '', longitude: '' });
      fetchCodes(); // Refresh codes after update
    } catch (error) {
      console.error('Erro ao atualizar código no Firebase:', error);
      setErrorMessage('Ocorreu um erro ao atualizar o código. Por favor, tente novamente.');
    }
  };

  const deleteCode = async (currentCode) => {
    try {
      const url = `https://geo-finder-7e641-default-rtdb.europe-west1.firebasedatabase.app/codes/${currentCode}.json`;
      await axios.delete(url);
      setSuccessMessage('Código deletado com sucesso!');
      fetchCodes(); // Refresh codes after deletion
    } catch (error) {
      console.error('Erro ao deletar código do Firebase:', error);
      setErrorMessage('Ocorreu um erro ao deletar o código. Por favor, tente novamente.');
    }
  };

  const handleCopyClick = async () => {
    try {
      await navigator.clipboard.writeText(code);
      setSuccessMessage('Código copiado para a área de transferência!');
    } catch (error) {
      console.error('Erro ao copiar código:', error);
      setErrorMessage('Ocorreu um erro ao copiar o código. Por favor, tente novamente.');
    }
  };

  const handleFrequencyChange = (selectedFrequency) => {
    if (!hasPaidPlan) {
      setShowPopup(true);
    } else {
      switch (selectedFrequency) {
        case '30min':
          intervalTimeRef.current = 30 * 60 * 1000;
          break;
        case '6hours':
          intervalTimeRef.current = 6 * 60 * 60 * 1000;
          break;
        default:
          intervalTimeRef.current = 24 * 60 * 60 * 1000;
          break;
      }
      setSuccessMessage(`Frequência de envio ajustada para ${selectedFrequency === '30min' ? '30 minutos' : '6 horas'}.`);
    }
  };

  const handlePopupClose = () => {
    setShowPopup(false);
  };

  const handleUpgradeClick = () => {
    navigate('/upgrade');
  };

  return (
    <div style={{ textAlign: 'center' }}>
      <div
        style={{
          backgroundImage: `url(${backgroundImage})`,
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          position: 'fixed',
          top: 0,
          left: 0,
          width: '100vw',
          height: '100vh',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center',
          alignItems: 'center',
          padding: '20px',
          boxSizing: 'border-box',
        }}
      >
        <h2 style={{ color: 'white', fontSize: '2em', marginBottom: '20px' }}>Gerador de Código</h2>
        <form onSubmit={handleSubmit} style={{ maxWidth: '100%', width: '300px' }}>
          <label style={{ color: 'white', display: 'block', marginBottom: '10px', fontSize: '1.2em' }}>
            Código Gerado:
            <input
              id="generatedCodeInput"
              type="text"
              value={code}
              readOnly
              style={{ marginLeft: '10px', borderRadius: '5px', padding: '5px', fontSize: '1em', width: '100%' }}
            />
          </label>
          {buttonDisabled ? (
            <>
              <button type="button" onClick={handleCopyClick} style={{ fontSize: '1em', padding: '10px 20px', marginBottom: '10px' }}>Copiar Código</button>
              <h3 style={{ color: 'white' }}>Escolha os tempos que a localização vai ser enviada!</h3>
              <div style={{ marginTop: '10px' }}>
                <button 
                  type="button" 
                  onClick={() => handleFrequencyChange('30min')} 
                  style={{ marginRight: '10px' }}
                >
                  30 min
                </button>
                <button 
                  type="button" 
                  onClick={() => handleFrequencyChange('6hours')}
                >
                  6h
                </button>
              </div>
            </>
          ) : (
            <button type="submit" style={{ fontSize: '1em', padding: '10px 20px', marginBottom: '10px' }}>Gerar e Enviar para o Firebase</button>
          )}
        </form>
        {successMessage && <p style={{ color: 'green', fontSize: '1em', marginTop: '10px' }}>{successMessage}</p>}
        {errorMessage && <p style={{ color: 'red', fontSize: '1em', marginTop: '10px' }}>{errorMessage}</p>}
        <Link
          to="/finder"
          style={{
            padding: '10px',
            backgroundColor: 'white',
            color: 'black',
            fontWeight: 700,
            display: 'inline-block',
            textDecoration: 'none',
            marginTop: '20px',
          }}
        >
          Finder
        </Link>
        <div style={{ marginTop: '20px', width: '300px' }}>
          <h3 style={{ color: 'white', marginBottom: '10px' }}>Atualizar Código</h3>
          <input
            type="text"
            placeholder="Código"
            value={updateCodeData.code}
            onChange={(e) => setUpdateCodeData({ ...updateCodeData, code: e.target.value })}
            style={{ marginBottom: '10px', padding: '5px', width: '100%' }}
          />
          <input
            type="text"
            placeholder="Latitude"
            value={updateCodeData.latitude}
            onChange={(e) => setUpdateCodeData({ ...updateCodeData, latitude: e.target.value })}
            style={{ marginBottom: '10px', padding: '5px', width: '100%' }}
          />
          <input
            type="text"
            placeholder="Longitude"
            value={updateCodeData.longitude}
            onChange={(e) => setUpdateCodeData({ ...updateCodeData, longitude: e.target.value })}
            style={{ marginBottom: '10px', padding: '5px', width: '100%' }}
          />
          <button
            type="button"
            onClick={updateCode}
            style={{ padding: '10px', width: '100%' }}
          >
            Atualizar Código
          </button>
        </div>
        <div style={{ marginTop: '20px', width: '300px' }}>
          <h3 style={{ color: 'white', marginBottom: '10px' }}>Lista de Códigos</h3>
          {codes.map((codeItem) => (
            <div key={codeItem.code} style={{ backgroundColor: 'white', marginBottom: '10px', padding: '10px', borderRadius: '5px' }}>
              <p style={{ margin: '0' }}><strong>Código:</strong> {codeItem.code}</p>
              <p style={{ margin: '0' }}><strong>Latitude:</strong> {codeItem.latitude}</p>
              <p style={{ margin: '0' }}><strong>Longitude:</strong> {codeItem.longitude}</p>
              <button
                onClick={() => deleteCode(codeItem.code)}
                style={{ marginTop: '10px', padding: '5px 10px', backgroundColor: 'red', color: 'white', borderRadius: '5px', cursor: 'pointer' }}
              >
                Deletar
              </button>
            </div>
          ))}
        </div>
        {showPopup && (
          <div
            style={{
              position: 'fixed',
              top: '50%',
              left: '50%',
              transform: 'translate(-50%, -50%)',
              backgroundColor: 'white',
              padding: '20px',
              boxShadow: '0px 0px 10px rgba(0, 0, 0, 0.1)',
              zIndex: 1000,
            }}
          >
            <h3>Plano Pago Necessário</h3>
            <p>Para ajustar a frequência de envio, é necessário um plano pago.</p>
            <button onClick={handlePopupClose} style={{ marginRight: '10px' }}>Fechar</button>
            <button onClick={handleUpgradeClick}>Fazer Upgrade</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default FirebaseCodeGenerator;
