import React, { useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import backgroundImage from './background.png';


const FirebaseCodeGenerator = () => {
  const [code, setCode] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const generateRandomCode = () => {
    const characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789!@#$%';
    const length = 10;
    let generatedCode = '';

    for (let i = 0; i < length; i++) {
      const randomIndex = Math.floor(Math.random() * characters.length);
      generatedCode += characters[randomIndex];
    }

    return generatedCode;
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      // Gerar código aleatório
      const generatedCode = generateRandomCode();

      // Atualizar o estado com o código gerado
      setCode(generatedCode);

      // Obter localização do usuário
      if ('geolocation' in navigator) {
        navigator.geolocation.getCurrentPosition(async (position) => {
          const { latitude, longitude } = position.coords;

          // Dados a serem enviados
          const data = {
            code: generatedCode,
            latitude: latitude,
            longitude: longitude
          };

          // URL com identificador único
          const url = `https://geo-finder-7e641-default-rtdb.europe-west1.firebasedatabase.app/codes/${generatedCode}.json`;

          // Envia os dados usando Axios com o método PUT
          await axios.put(url, data);

          setSuccessMessage('Código enviado com sucesso!');
        }, (error) => {
          console.error('Erro ao obter localização:', error);
          setErrorMessage('Ocorreu um erro ao obter sua localização. Por favor, tente novamente.');
        });
      } else {
        setErrorMessage('Geolocalização não suportada pelo seu navegador.');
      }
    } catch (error) {
      console.error('Erro ao enviar código para o Firebase:', error);
      setErrorMessage('Ocorreu um erro ao enviar o código. Por favor, tente novamente.');
    }
  };

  return (
    <div style={{ textAlign: 'center', maxWidth: '400px', margin: 'auto' }}>
<div
  style={{
    backgroundImage: `url(${backgroundImage})`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    position: 'fixed',
    top: 0,
    left: 0,
    width: '100%',
    height: '100%',
  }}
>


      <h2 style={{color: 'white',}} >Gerador de Código</h2>
      <form onSubmit={handleSubmit}>
        <label style={{ color: 'white', display: 'block', marginBottom: '10px' }}>
          Código Gerado:
          <input
            type="text"
            value={code}
            readOnly
            style={{ marginLeft: '10px', borderRadius: 5, }}
          />
        </label>
        <button type="submit" style={{ marginBottom: '10px' }}>Gerar e Enviar para o Firebase</button>
      </form>
      {successMessage && <p style={{ color: 'green' }}>{successMessage}</p>}
      {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}
      <Link to="/finder" style={{ padding: '10px', backgroundColor: 'white', color: 'black', fontWeight: 700, marginLeft: '10px', display: 'inline-block', textDecoration: 'none', marginTop: '10px' }}>
        Ir para Finder
      </Link>
      </div>
    </div>
  );
};

export default FirebaseCodeGenerator;
