import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import backgroundImage from './background.png';

const FirebaseCodeGenerator = () => {
  const [code, setCode] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [buttonDisabled, setButtonDisabled] = useState(false); // Controla se o botão de gerar está habilitado
  const [frequency, setFrequency] = useState('daily'); // Estado para armazenar a frequência escolhida ('30min', '2hours', '12hours', 'daily')

  let intervalTime = 24 * 60 * 60 * 1000; // Padrão para envio diário em milissegundos

  useEffect(() => {
    // Verifica se há um código salvo nos cookies/local storage ao carregar o componente
    const savedCode = localStorage.getItem('generatedCode');
    if (savedCode) {
      setCode(savedCode);
      setButtonDisabled(true); // Desativa o botão de gerar
    }

    // Configura o cronômetro com base na frequência escolhida
    switch (frequency) {
      case '30min':
        intervalTime = 30 * 60 * 1000; // 30 minutos em milissegundos
        break;
      case '2hours':
        intervalTime = 2 * 60 * 60 * 1000; // 2 horas em milissegundos
        break;
      case '12hours':
        intervalTime = 12 * 60 * 60 * 1000; // 12 horas em milissegundos
        break;
      case 'daily':
      default:
        intervalTime = 24 * 60 * 60 * 1000; // 24 horas em milissegundos
        break;
    }

    // Inicializa o cronômetro para enviar a localização para o Firebase
    const interval = setInterval(() => {
      sendLocationToFirebase();
    }, intervalTime);

    // Retorna uma função de limpeza para parar o cronômetro ao desmontar o componente
    return () => clearInterval(interval);
  }, [frequency]);

  const generateRandomCode = () => {
    const characters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789!@#$%';
    const length = 10;
    let generatedCode = '';

    for (let i = 0; i < length; i++) {
      const randomIndex = Math.floor(Math.random() * characters.length);
      generatedCode += characters[randomIndex];
    }

    return generatedCode;
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      let generatedCode = code;
      if (!generatedCode) {
        generatedCode = generateRandomCode();
        setCode(generatedCode);
        localStorage.setItem('generatedCode', generatedCode); // Salva o código nos cookies/local storage
        setButtonDisabled(true); // Desativa o botão de gerar após a primeira geração
      }

      await sendLocationToFirebase();

      setSuccessMessage('Código enviado com sucesso!');
    } catch (error) {
      console.error('Erro ao enviar código para o Firebase:', error);
      setErrorMessage('Ocorreu um erro ao enviar o código. Por favor, tente novamente.');
    }
  };

  const sendLocationToFirebase = async () => {
    try {
      if ('geolocation' in navigator) {
        navigator.geolocation.getCurrentPosition(async (position) => {
          const { latitude, longitude } = position.coords;

          const data = {
            code: code,
            latitude: latitude,
            longitude: longitude
          };

          const url = `https://geo-finder-7e641-default-rtdb.europe-west1.firebasedatabase.app/codes/${code}.json`;

          await axios.put(url, data);

          console.log('Localização enviada para o Firebase:', data);
        }, (error) => {
          console.error('Erro ao obter localização:', error);
        });
      } else {
        console.log('Geolocalização não suportada pelo seu navegador.');
      }
    } catch (error) {
      console.error('Erro ao enviar localização para o Firebase:', error);
    }
  };

  const handleCopyClick = async () => {
    try {
      await navigator.clipboard.writeText(code);
      setSuccessMessage('Código copiado para a área de transferência!');
    } catch (error) {
      console.error('Erro ao copiar código:', error);
      setErrorMessage('Ocorreu um erro ao copiar o código. Por favor, tente novamente.');
    }
  };

  const handleFrequencyChange = (selectedFrequency) => {
    setFrequency(selectedFrequency);
  };

  return (
    <div style={{ textAlign: 'center' }}>
      <div
        style={{
          backgroundImage: `url(${backgroundImage})`,
          backgroundSize: 'cover',
          backgroundPosition: 'center',
          position: 'fixed',
          top: 0,
          left: 0,
          width: '100vw',
          height: '100vh',
          display: 'flex',
          flexDirection: 'column',
          justifyContent: 'center',
          alignItems: 'center',
          padding: '20px',
          boxSizing: 'border-box',
        }}
      >
        <h2 style={{ color: 'white', fontSize: '2em', marginBottom: '20px' }}>Gerador de Código</h2>
        <form onSubmit={handleSubmit} style={{ maxWidth: '100%', width: '300px' }}>
          <label style={{ color: 'white', display: 'block', marginBottom: '10px', fontSize: '1.2em' }}>
            Código Gerado:
            <input
              id="generatedCodeInput"
              type="text"
              value={code}
              readOnly
              style={{ marginLeft: '10px', borderRadius: '5px', padding: '5px', fontSize: '1em', width: '100%' }}
            />
          </label>
          {buttonDisabled ? (
            <>
              <button type="button" onClick={handleCopyClick} style={{ fontSize: '1em', padding: '10px 20px', marginBottom: '10px' }}>Copiar Código</button>
              asfdas
              <div style={{ marginTop: '10px' }}>
                <button type="button" onClick={() => handleFrequencyChange('30min')} style={{ marginRight: '10px' }}>30 min</button>
                <button type="button" onClick={() => handleFrequencyChange('2hours')} style={{ marginRight: '10px' }}>2h</button>
                <button type="button" onClick={() => handleFrequencyChange('12hours')} style={{ marginRight: '10px' }}>12h</button>
                <button type="button" onClick={() => handleFrequencyChange('daily')}>24h</button>
              </div>
            </>
          ) : (
            <button type="submit" style={{ fontSize: '1em', padding: '10px 20px', marginBottom: '10px' }}>Gerar e Enviar para o Firebase</button>
          )}
        </form>
        {successMessage && <p style={{ color: 'green', fontSize: '1em', marginTop: '10px' }}>{successMessage}</p>}
        {errorMessage && <p style={{ color: 'red', fontSize: '1em', marginTop: '10px' }}>{errorMessage}</p>}
        <Link
          to="/finder"
          style={{
            padding: '10px',
            backgroundColor: 'white',
            color: 'black',
            fontWeight: 700,
            display: 'inline-block',
            textDecoration: 'none',
            marginTop: '10px',
            fontSize: '1em',
          }}
        >
          Ir para Finder
        </Link>
      </div>
    </div>
  );
};

export default FirebaseCodeGenerator;
